import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams, useHistory, Link } from "react-router-dom";
import styled from 'styled-components'
import usePokemons from "../Hooks/usePokemons"



function Home(props) {
  const history = useHistory();
  const pathParams = useParams();
  const name = pathParams;

  const pokemons = usePokemons("https://pokeapi.co/api/v2/pokemon/?limit=40", []);
  const { listPokedex, setListPokedex, listHome, setListHome } = props;

  useEffect(() => {
    detailsPokemons();
  }, [pokemons]);

  const detailsPokemons = async () => {
    let copyArray = [];
    try {
      for (const pokemon of pokemons) {
        const res = await axios.get(
          `https://pokeapi.co/api/v2/pokemon/${pokemon.name}`
        );
        copyArray.push(res.data);
        console.log(res.data);
      }
    } catch (error) {
      console.log(error);
    }

    if (listHome.length === 0 && listPokedex.length === 0) {
      setListHome(copyArray);
    }
  };

  const setPokedex = (pokemon) => {

    const index = listHome.findIndex((i) => i.id === pokemon.id);
    const newPokedex = [...listPokedex, pokemon];
    setListPokedex(newPokedex);
    listHome.splice(index, 1);
  };

  const goToDetails = (id) => {
    history.push(`/details/${id}`);
  };

  return (
    <Container>
      <PokemonGrid>
      {listHome.map((pokemon) => {
        return (
          <PokemonCard key={pokemon.name}>
            <Link className="card-link" to={`/details/${pokemon.name}`}>
            <img src={pokemon.sprites.front_default}/>
            <h3>{pokemon.name}</h3>
            </Link>
            
      
            
          <ButtonContainer>
            <Button onClick={() => setPokedex(pokemon)}>
              Adicionar à Pokedex
            </Button>

            <Link to={`/details/${pokemon.name}`}>
              <Button onClick={() => goToDetails(pokemon.id)}>
                {" "}
                Ver detalhes
              </Button>
            </Link>
            </ButtonContainer>
          </PokemonCard>
        );
      })}
      </PokemonGrid>
    </Container>
  );
}

export default Home;

const Container = styled.div`
  border: 120px solid red;
  
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`

const PokemonGrid = styled.div `
  border: 3px 
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr 1fr;
  grid-column-gap: 1rem;
  grid-row-gap: 1rem;
  margin: 1.5rem;
  @media screen and (max-width: 900px) {
  grid-template-columns: 1fr 1fr 1fr 1fr;
  margin: 1rem;
}
@media screen and (max-width: 600px) {
  grid-template-columns: 1fr 1fr;
}
`

const PokemonCard = styled.div `
  display: flex;
  flex-direction: column;
  border: 1px solid #96031A;
  height: 35vh;
  width: 10vw;
  padding: 1.5rem;
  font-size: 1.3rem;
  align-items: center;
  justify-content: center;
  background-color: #FDF0D5;
  h3{
    text-transform: capitalize;
  }
  .card-link {
    color: black;
    text-decoration: none;
    text-align: center;
  }
  &:hover {
    cursor: pointer;
    transform: scale(1.05);
  }
  @media screen and (max-width: 900px) {
    height: 55vh;
    width: 15vw;
}
  
  @media screen and (max-width: 600px) {
  height: 50vh;
  width: 35vw;
}
`

const ButtonContainer = styled.div `
  display: flex;
  flex-direction: column;
  margin-bottom: 1rem;
`


const Button = styled.button `
 grid-column-start: 2;
grid-row-start: 3;
border-radius: 8px;
font-size: 1rem;
border:none;
cursor: pointer;
border: 1px solid ${props => props.color};
border-bottom: 50%;
width: 124px;
height:50px;
padding: 0.35rem; 
:hover{
    transform: scale(1.2);
    transition: all 0.5s;
    background-color: ${props => props.color};
}
:active{
    background-color: ${props => props.activeColor};
}
`
