import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams, useHistory, Link } from "react-router-dom";
import styled from 'styled-components';

function Pokedex(props) {
  const history = useHistory();
  const pathParams = useParams();
  const name = pathParams;

  const { listPokedex, listHome, setListHome } = props;

  const deletPokemon = (pokemon) => {
    const index = listPokedex.findIndex((i) => i.id === pokemon.id);
    const newPokedex = [...listHome, pokemon];
    setListHome(newPokedex);
    listPokedex.splice(index, 1);

    if (listPokedex === 0) {
      history.push("/")
    }
  };

  const choosePokemon = () => {
    axios.get(`https://pokeapi.co/api/v2/pokemon/${name}`).then(() => {
      history.push("/details");
    });
  };

  return (
    <Container>
      <PokemonGrid>
      {listPokedex === 0 ? <EmptyPokedex><h2 style={{textAlign: "center"}}>A pokedex esta vazia!</h2><h2> <Link className="empty-link" to="/">Adicione alguns pokemons! </Link></h2></EmptyPokedex> : listPokedex.map((pokemon) => {
        return (
          <PokemonCard>
            <img src={pokemon.sprites.front_default}></img>

            <p>{pokemon.name}</p>
          <ButtonContainer>
            <Button onClick={() => deletPokemon(pokemon)}>
              Remover Pokemon
            </Button>

            <Link to={`/details/${pokemon.name}`}>
              <Button onClick={choosePokemon}>Ver detalhes</Button>
            </Link>
          </ButtonContainer>
            </PokemonCard>
        );
      })}
      </PokemonGrid>
      
    </Container>
  );
}

export default Pokedex;

const Container = styled.div`
  background-image: #F8F4F9;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`

const PokemonGrid = styled.div `
  border: 2px solid black;
  background-color: black;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr 1fr;
  grid-column-gap: 1rem;
  grid-row-gap: 1rem;
  margin: 1.5rem;
  @media screen and (max-width: 900px) {
  grid-template-columns: 1fr 1fr 1fr 1fr;
  margin: 1rem;
}
@media screen and (max-width: 600px) {
  grid-template-columns: 1fr 1fr;
}
`

const PokemonCard = styled.div `
  display: flex;
  flex-direction: column;
  border: 1px solid yellow;
  height: 35vh;
  width: 10vw;
  padding: 1.5rem;
  font-size: 1.3rem;
  align-items: center;
  justify-content: center;
  background-color: #FDF0D5;
  h3{
    text-transform: capitalize;
  }
  .card-link {
    color: black;
    text-decoration: none;
    text-align: center;
  }
  &:hover {
    cursor: pointer;
    transform: scale(1.05);
  }
  @media screen and (max-width: 900px) {
    height: 55vh;
    width: 15vw;
}
  
  @media screen and (max-width: 600px) {
  height: 50vh;
  width: 35vw;
}
`

const ButtonContainer = styled.div `
  display: flex;
  flex-direction: column;
  margin-bottom: 1rem;
  
`

const Button = styled.button `
grid-column-start: 2;
grid-row-start: 3;
border-radius: 8px;
font-size: 1rem;
border:none;
cursor: pointer;
border: 1px solid ${props => props.color};
border-bottom: 50%;
width: 1r0px;
height: 50px;
padding: 0.35rem; 
:hover{
    transform: scale(1.2);
    transition: all 0.5s;
    background-color: ${props => props.color};
}
:active{
    background-color: ${props => props.activeColor};
}
`

const EmptyPokedex = styled.div `
  display-inside: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  .empty-link {
    text-align: center;
    text-decoration: underline;
    color: white;
  }
`
